import { response } from 'express';
import pool from '../Database/mysql';
import axios from 'axios'; // Pour faire des requêtes HTTP à CinetPay

export const addNewOrders = async (req, res = response) => {
  try {
    const { uidAddress, total, typePayment, products, instructions } = req.body;

    // Si le paiement est par Mobile Money, déclencher CinetPay
    if (typePayment === 'Mobile Money') {
      const cinetpayResponse = await initiateCinetpayPayment(total, req.uid);

      if (cinetpayResponse.status !== 'ACCEPTED') {
        return res.status(400).json({
          resp: false,
          msg: 'Le paiement a échoué.',
        });
      }
    }

    // Ajouter la commande à la base de données
    const orderdb = await pool.query(
      'INSERT INTO orders (client_id, address_id, amount, pay_type, special_instructions) VALUES (?,?,?,?,?)',
      [req.uid, uidAddress, total, typePayment, instructions]
    );

    // Ajouter les détails de la commande
    products.forEach(o => {
      pool.query(
        'INSERT INTO orderDetails (order_id, product_id, quantity, price) VALUES (?,?,?,?)',
        [orderdb.insertId, o.uidProduct, o.quantity, o.quantity * o.price]
      );
    });

    res.json({
      resp: true,
      msg: 'New Order added successfully',
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

// Fonction pour initier le paiement CinetPay
const initiateCinetpayPayment = async (amount, customerId) => {
  const transactionId = Date.now().toString(); // Générer un ID de transaction unique
  const cinetpayData = {
    apikey: 'VOTRE_API_KEY', // Remplacez par votre clé API CinetPay
    site_id: 'VOTRE_SITE_ID', // Remplacez par votre site ID
    transaction_id: transactionId,
    amount: amount,
    currency: 'XOF',
    channels: 'ALL',
    description: 'Paiement pour commande',
    customer_id: customerId,
    notify_url: 'https://votre-domaine.com/cinetpay-notify', // Remplacez par votre NOTIFY_URL
  };

  try {
    const response = await axios.post('https://api.cinetpay.com/v2/payment', cinetpayData);
    return response.data;
  } catch (error) {
    console.error('Erreur lors de l\'initiation du paiement CinetPay :', error);
    throw error;
  }
};

// Les autres fonctions restent inchangées
export const getOrdersByStatus = async (req, res = response) => {
  try {
    const ordersdb = await pool.query(`CALL SP_ALL_ORDERS_STATUS(?);`, [req.params.statusOrder]);
    res.json({
      resp: true,
      msg: 'Orders by ' + req.params.statusOrder,
      ordersResponse: ordersdb[0],
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

export const getDetailsOrderById = async (req, res = response) => {
  try {
    const detailOrderdb = await pool.query(`CALL SP_ORDER_DETAILS(?);`, [req.params.idOrderDetails]);

    res.json({
      resp: true,
      msg: 'Order details by ' + req.params.idOrderDetails,
      detailsOrder: detailOrderdb[0],
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

export const updateStatusToDispatched = async (req, res = response) => {
  try {
    const { idDelivery, idOrder } = req.body;

    await pool.query('UPDATE orders SET status = ?, delivery_id = ? WHERE id = ?', [
      'ATTRIBUÉE',
      idDelivery,
      idOrder,
    ]);

    res.json({
      resp: true,
      msg: 'Commande attribuée',
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

export const getOrdersByDelivery = async (req, res = response) => {
  try {
    const ordersDeliverydb = await pool.query(`CALL SP_ORDERS_BY_DELIVERY(?,?);`, [
      req.uid,
      req.params.statusOrder,
    ]);
    res.json({
      resp: true,
      msg: 'All Orders By Delivery',
      ordersResponse: ordersDeliverydb[0],
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

export const updateStatusToOntheWay = async (req, res = response) => {
  try {
    const { latitude, longitude } = req.body;

    await pool.query('UPDATE orders SET status = ?, latitude = ?, longitude = ? WHERE id = ?', [
      'EN ROUTE',
      latitude,
      longitude,
      req.params.idOrder,
    ]);

    res.json({
      resp: true,
      msg: 'Commande en route',
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

export const updateStatusToDelivered = async (req, res = response) => {
  try {
    await pool.query('UPDATE orders SET status = ? WHERE id = ?', ['LIVRÉE', req.params.idOrder]);

    res.json({
      resp: true,
      msg: 'Commande livrée',
    });
  } catch (e) {
    return res.status(500).json({
      resp: false,
      msg: e,
    });
  }
};

