import { response } from 'express';
import pool from '../Database/mysql';
import { addNewOrders } from './OrdersController'; // Importez la fonction existante

export const handleCinetpayNotification = async (req, res = response) => {
  try {
    const paymentData = req.body;

    console.log('Notification reçue de CinetPay :', paymentData);

    // Vérifiez si le paiement est réussi
    if (paymentData.status === 'ACCEPTED') {
      console.log('Paiement réussi !');

      // Exemple de données pour une nouvelle commande
      const orderData = {
        uidAddress: paymentData.customer_id, // Remplacez par le champ approprié
        total: paymentData.amount,
        typePayment: 'Mobile Money',
        products: [], // Remplacez par les produits de la commande
        instructions: 'Paiement via CinetPay',
      };

      // Ajoutez la nouvelle commande en utilisant la fonction existante
      // req.uid est fourni par verifyToken et représente l'ID de l'utilisateur
      await addNewOrders({ body: orderData, uid: req.uid }, res);
    } else {
      console.log('Paiement échoué ou annulé.');
    }

    // Répondez à CinetPay pour confirmer la réception de la notification
    res.status(200).json({ message: 'Notification reçue avec succès.' });
  } catch (e) {
    console.error('Erreur lors du traitement de la notification CinetPay :', e);
    res.status(500).json({ message: 'Erreur interne du serveur.' });
  }
};

